# Namespace module for gem
module Adyen
  # Namespace for payments objects that are used throughout the Adyen API
  module Payments
    # Holds results of performing an individual risk check
    #
    # @!attribute [rw] account_score
    #   @return [Integer] The fraud score generated by the risk check
    # @!attribute [rw] check_id
    #   @return [Integer] The id of the risk check
    # @!attribute [rw] name
    #   @return [String] The name of the risk check
    #
    class FraudCheckResult < ApiObject
      attributes :account_score, :check_id, :name

      validates :account_score, :check_id, :name, presence: true
      validates :account_score, :check_id, numericality: { only_integer: true }
    end
  end
end
