# Namespace module for gem
module Adyen
  # Namespace for payments objects that are used throughout the Adyen API
  module Payments
    # Holds information on the results of a fraud risk check
    #
    # @!attribute [rw] account_score
    #   @return [Integer] The total fraud score generated by the risk checks
    # @!attribute [rw] results
    #   @return [Array] Array of FraudCheckResult. The result of the individual risk checks.
    #
    class FraudResult < ApiObject
      attributes :account_score, :results

      validates :account_score, presence: true
      validates :account_score, :check_id, numericality: { only_integer: true }
    end
  end
end
